"use strict";
var router = require('express').Router();
var checkToken = require('./helpers/checkToken');
var getRecurringsController = require('../controllers/recurrings/getRecurringsController');
var postRecurringController = require('../controllers/recurrings/postRecurringController');
var updateRecurringController = require('../controllers/recurrings/updateRecurringController');
var postCopyRecurrings = require('../controllers/recurrings/postCopyRecurrings');
var deleteRecurringController = require('../controllers/recurrings/deleteRecurringController');
router.get('/', checkToken, getRecurringsController);
router.post('/', checkToken, postRecurringController);
router.put('/:id', checkToken, updateRecurringController);
router.post('/copy', checkToken, postCopyRecurrings);
router.delete('/:id', checkToken, deleteRecurringController);
module.exports = router;
