"use strict";
var router = require('express').Router();
var checkToken = require('./helpers/checkToken');
var uploadMiddleware = require('./helpers/customMulter');
var getSpendingsController = require('../controllers/spendings/getSpendingsController');
var getSpendingController = require('../controllers/spendings/getSpendingController');
var getSpendingInvoiceImageController = require('../controllers/spendings/getSpendingInvoiceImageController');
var getSpendingsChartsController = require('../controllers/spendings/getSpendingsChartsController');
var postSpendingController = require('../controllers/spendings/postSpendingController');
var postSpendingInvoiceUploadController = require('../controllers/spendings/postSpendingInvoiceUploadController');
var deleteSpendingInvoiceImageController = require('../controllers/spendings/deleteSpendingInvoiceImageController');
var updateSpendingController = require('../controllers/spendings/updateSpendingController');
var deleteSpendingController = require('../controllers/spendings/deleteSpendingController');
router.get('/', checkToken, getSpendingsController);
router.get('/charts', checkToken, getSpendingsChartsController);
router.get('/upload/:id', checkToken, getSpendingInvoiceImageController);
router.get('/:id', checkToken, getSpendingController);
router.post('/', checkToken, postSpendingController);
router.post('/upload', [checkToken, uploadMiddleware.single('invoiceImageUpload')], postSpendingInvoiceUploadController);
router.put('/upload', checkToken, deleteSpendingInvoiceImageController);
router.put('/:id', checkToken, updateSpendingController);
router.delete('/:id', checkToken, deleteSpendingController);
module.exports = router;
